import { useEffect, useState } from "react";
import axios from 'axios';


interface Props {
    latitude: number;
    longitude: number;
    forecastDays: number;
};  

const WeatherCard: React.FC<Props> = ({lat, lng, forecastDays}) => {
    const API_URL_DAILY = `https://api.open-meteo.com/v1/forecast?latitude=${lat}&longitude=${lng}&daily=weathercode,temperature_2m_max,temperature_2m_min,apparent_temperature_max,apparent_temperature_min,sunrise,sunset,uv_index_max,uv_index_clear_sky_max,precipitation_sum,rain_sum,showers_sum,snowfall_sum,windspeed_10m_max,windgusts_10m_max,winddirection_10m_dominant&current_weather=true&timezone=auto&forecast_days=${forecastDays}`;
    
    const [weatherData, setWeatherData] = useState([]);
    const [searchQuery, setSearchQuery] = useState("");

    useEffect(() => {
        const fetchWeatherData = async () => {
            try {
                const response = await axios.get(API_URL_DAILY);
                console.log(response.data);
                setWeatherData(response.data);
            }
            catch(error) {
                console.error("Error fetching weather data. " + error);
            }
        };
        fetchWeatherData();
    }, []);

    return (
        <>
            <div className="weather-card">
                <h1>Weather Card Component</h1>
                <form>
                    <label>
                        <input type="text" placeholder="Search City"></input>
                    </label>
                </form>
                <p>[weather data]</p>
            </div>
        </>
    );
};

export default WeatherCard;
