import React, { RefObject, useEffect, useRef, useState } from "react";

interface Props {
    minTemp: number;
    maxTemp: number;
    currMinTemp: number;
    currMaxTemp: number;
    currTemp: number;
}

const TemperatureBar: React.FC<Props> = ({ minTemp, maxTemp, currMinTemp, currMaxTemp, currTemp}): JSX.Element => {
    const temperatureBarFullElementRef = useRef<HTMLDivElement>(null);
    const [widthTemperatureBarCurr, setWidthTemperatureBarCurr] = useState(0);

    useEffect(() => {
        if (temperatureBarFullElementRef.current) {
            const widthTemperatureBarFull = temperatureBarFullElementRef.current.offsetWidth;
            const newWidth = ((currMaxTemp - currMinTemp) * widthTemperatureBarFull) / (maxTemp - minTemp);
            setWidthTemperatureBarCurr(newWidth);
        }
    }, [])

    return (
        <div className="temperature-bar-container">
            <p>{currMinTemp}°</p>
            <div className="temperature-bar-full" ref={temperatureBarFullElementRef}>
                <div className="temperature-bar-current" ref={temperatureBarCurrElementRef}>
                    {/* width of the curr bar = ((currMax - currMin) * width of the full bar) / max - min */}
                </div>
            </div>
            <p>{currMaxTemp}°</p>
        </div>
    )
}

export default TemperatureBar;
