import { useState, useEffect, useRef } from "react";
import axios from "axios";

interface Props {
    latitude : number;
    longitude: number;
}

interface WeatherData {
    // TODO
};

interface searchQueryResponse {
    // TODO
};

const WeatherCard: React.FC<Props> = ({ latitude, longitude }) => {
    const MAX_CITY_RESPONSE_NUMBER = 10;

    const [weatherData, setWeatherData]= useState<any | undefined>(undefined); // TODO: Change 'any' to 'WeatherData'
    const [searchQuery, setSearchQuery] = useState<string>("");
    const [searchQueryResponse, setSearchQueryResponse] = useState<any | undefined>([]); // TODO: Change 'any' to 'SearchQueryResponse'
    const [selectedCity, setSelectedCity] = useState<string>("");
    const [selectedCityCoordinates, setSelectedCityCoordinates] = useState<number[]>([]);
    const [searchDropdownOpen, setSearchDropdownOpen] = useState<boolean>(false);

    useEffect(() => {
        const fetchWeatherData = async () => {
            try {
                const response = await axios.get(`https://api.open-meteo.com/v1/forecast?latitude=${latitude}&longitude=${longitude}&hourly=temperature_2m,uv_index&daily=weathercode,temperature_2m_max,temperature_2m_min,apparent_temperature_max,apparent_temperature_min,sunrise,sunset,uv_index_max,uv_index_clear_sky_max,precipitation_sum,rain_sum,showers_sum,snowfall_sum,windspeed_10m_max,windgusts_10m_max,winddirection_10m_dominant&current_weather=true&timezone=auto&forecast_days=16`);
                // console.log(response.data);
                setWeatherData(response.data);
            } catch (error) {
                console.error("Error fetching weather data. " + error);
            }
        };
        fetchWeatherData(); 
    }, []);

    useEffect(() => {
        const fetchSearchQueryResponse = async () => {
            try {
                const response = await axios.get(`https://geocoding-api.open-meteo.com/v1/search?name=${searchQuery}&count=10&language=en&format=json`);
                console.log(response.data);
                setSearchQueryResponse(response.data);
            } catch (error) {
                console.error("Error fetching geolocation data. " + error);
            }
        };
        fetchSearchQueryResponse();
    }, [searchQuery]);

    const handleOnSearchQueryChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setSearchQuery(event.target.value);
    };

    const handleOnSearchQueryClick = () => {
        setSearchDropdownOpen(true);
    };

    const handleOnSearchQueryResponseClick = (name: string) => {
        setSelectedCity(name);
    };

    const handleOnSubmit = (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
        if (searchQueryResponse.results) {
            setSelectedCity(searchQueryResponse.results[0].name);
            // setSearchQuery("");
        }
    };

    return (
        <>
            <div className="weather-card">
                <div className="weather-card-search">
                    <form
                        className="weather-card-search-form"
                        onSubmit={handleOnSubmit}
                    >
                            <input
                                className="weather-card-search-box"
                                type="text"
                                value={searchQuery}
                                onChange={handleOnSearchQueryChange}
                                onClick={handleOnSearchQueryClick}
                                placeholder={"Search by city name"}
                            ></input>
                        <div className="weather-card-search-dropdown">
                            {!selectedCity && searchQueryResponse?.results?.map((result: any, index: number) => (
                                <div className="weather-card-search-dropdown-item"
                                    onClick={() => handleOnSearchQueryResponseClick(result.name)} key={index}
                            >
                                    {result.name}
                                    <span style={{'fontSize': '12px'}}>
                                        , {result.country_code}
                                    </span>
                                </div>
                            ))}
                        </div>
                    </form>     
                </div>
                {weatherData && (
                    <>
                        <div className="weather-card-current-main">
                            {/* <p>{weatherData.current_weather.temperature}℃</p> */}
                        </div>
                        <div className="weather-card-hourly"></div>
                        <div className="weather-card-daily"></div>
                    </>
                )}
                {/* <p>{selectedCity}</p> */}
            </div>
        </>
    );
};

export default WeatherCard;
