import { useEffect, useState } from "react";
import axios from 'axios';

interface Props {
    latitude: number;
    longitude: number;
};  

const WeatherCard: React.FC<Props> = ({ latitude, longitude }) => {
    const API_URL = `https://api.open-meteo.com/v1/forecast?latitude=${latitude}&longitude=${longitude}&hourly=temperature_2m,uv_index&daily=weathercode,temperature_2m_max,temperature_2m_min,apparent_temperature_max,apparent_temperature_min,sunrise,sunset,uv_index_max,uv_index_clear_sky_max,precipitation_sum,rain_sum,showers_sum,snowfall_sum,windspeed_10m_max,windgusts_10m_max,winddirection_10m_dominant&current_weather=true&timezone=auto&forecast_days=16`;

    const [weatherData, setWeatherData] = useState<string[] | null>(null);
    const [searchQuery, setSearchQuery] = useState("");

    useEffect(() => {
        const fetchWeatherData = async () => {
            try {
                const response = await axios.get(API_URL);
                console.log(response.data);
                setWeatherData(response.data);
            }
            catch(error) {
                console.error("Error fetching weather data. " + error);
            }
        };
        fetchWeatherData();
    }, []);

    return (
        <>
            <div className="weather-card">
                <div className="weather-card-search">
                    <form>
                        <label>
                            <input type="text" placeholder="Search City"></input>
                        </label>
                    </form>
                </div>
                { weatherData && (
                    <>
                        <div className="weather-card-header">
                            <p>[City, Country]</p>
                            <p>{weatherData.currentWeather}</p>
                        </div>
                        <div className="weather-card-hourly">

                        </div>
                        <div className="weather-card-daily">

                        </div>
                    </>
                )}
            </div>
        </>
    );
};

export default WeatherCard;
