import weatherCodes from "../weatherCodes";

interface Props {
    hourlyWeather: any[];
    sunrise: string;
    sunset: string
}

const HourlyWeatherCardList: React.FC<Props> = ({ hourlyWeather, sunrise, sunset }) => {
    const isSunriseOrSunset = (card: any): boolean => {
        return card.hour === sunrise.slice(0, 2) || card.hour === sunset.slice(0, 2);
    }

    const displayTemperature = (card: any) => {
        if (isSunriseOrSunset(card)) {
            if (card.hour === sunrise.slice(0, 2))
                return <p>Sunrise</p>
            else if (card.hour === sunset.slice(0, 2)) 
                return <p>Sunset</p>
        }
        return <p>{ card.temperature }Â°</p>;
    }

    const displayHour = (card: any, index: number) => {
        if (index !== 0) { 
            if (isSunriseOrSunset(card)) {
                if (card.hour === sunrise.slice(0, 2))
                    return <p>{ sunrise }</p>
                else if (card.hour === sunset.slice(0, 2)) 
                    return <p>{ sunset }</p>
            }
            return <p>{ card.hour }</p> 
        } 
        else if (index === 0) { 
            return <p style={{ fontWeight: "bold" }}>Now</p>
        }
    }

    const displayIcon = (card: any) => {
        if (isSunriseOrSunset(card)) {
            if (card.hour === sunrise.slice(0, 2))
            // ./${code}.png
                return <img
                    src='./Assets/Images/0.png'
                    alt='sunrise'
                />
                else if (card.hour === sunset.slice(0, 2)) 
                    return <p>{ sunset }</p>
        }
        else {
            return <img
                        src={ weatherCodes[card.weatherCode].icon }
                        alt={`Weather icon ${card.weatherCode}`}
                    />
        }
    }

    return (
        <div className="hourly-weather-card-list-container">
            <div className="hourly-weather-card-list-header">
                <p>Hourly Weather - Next 24 Hours</p>
            </div>
            <div className="hourly-weather-card-list">
                { hourlyWeather.map((card, index) => (
                    <div key={ index } className="hourly-weather-card">
                        { displayHour(card, index) }
                        { displayIcon(card) }
                        { displayTemperature(card) }
                    </div>
                )) }
            </div>
        </div>
    );
};

export default HourlyWeatherCardList;
