import weatherCodes from "../weatherCodes";

interface Props {
    cards: any[];
}

const HourlyWeatherCardList: React.FC<Props> = ({ cards }) => {
    return (
        <div
            className="hourly-weather-card-list-container">
            <div className="hourly-weather-card-list-header">
                <p>Hourly weather - See the weather in the next 24 hours</p>
            </div>
            <div className="hourly-weather-card-list">
                {cards.map((card, index) => (
                    <div key={index} className="hourly-weather-card">
                        <p>{index !== 0 ? card.hour : "Now"}</p>
                        <img src={weatherCodes[card.weatherCode.icon]}>X</img>
                        <p>{card.temperature}Â°</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default HourlyWeatherCardList;
