import { useEffect, useState } from "react";
import axios from 'axios';

interface Props {
    lat: number;
    lng: number;
};  

const WeatherCard: React.FC<Props> = ({lat, lng}) => {

    const [weatherData, setWeatherData] = useState([]);

    useEffect(() => {
        const fetchWeatherData = async () => {
            try {
                const response = await axios.get(`https://api.open-meteo.com/v1/forecast?latitude=${lat}&longitude=${lng}&hourly=temperature_2m,apparent_temperature,precipitation_probability,surface_pressure,cloudcover,windgusts_10m&current_weather=true`);
                console.log(response.data);
                setWeatherData(response.data);
            }
            catch(error) {
                console.error("Error fetching weather data. " + error);
            }
        };
        fetchWeatherData();
    }, []);

    return (
        <>
            <div className="weather-card">
                <h1>Weather Card Component</h1>
                <form>
                    <label>
                        <input type="text"></input>
                    </label>
                </form>
                <p>[weather data]</p>
            </div>
        </>
    );
};

export default WeatherCard;
