import React, { RefObject, useEffect, useRef, useState } from "react";

interface Props {
    minTemp: number;
    maxTemp: number;
    currMinTemp: number;
    currMaxTemp: number;
    currTemp: number;
}

const TemperatureBar: React.FC<Props> = ({ minTemp, maxTemp, currMinTemp, currMaxTemp, currTemp}): JSX.Element => {
    const temperatureBarFullElement: React.RefObject<HTMLDivElement> = useRef<HTMLDivElement>(null);
    const temperatureBarCurr = useRef<HTMLDivElement>(null);

    const [widthTemperatureBarFull, setWidthTemperatureBarFull] = useState(0);
    const [widthTemperatureBarCurr, setWidthTemperatureBarCurr] = useState(0);


    useEffect(() => {
        setWidthTemperatureBarFull(temperatureBarFullElement.current?.offsetWidth);
    }, [])

    return (
        <div className="temperature-bar-container">
            <p>{currMinTemp}°</p>
            <div className="temperature-bar-full">
                <div className="temperature-bar-current">
                    {/* width of the curr bar = ((currMax - currMin) * width of the full bar) / max - min */}
                </div>
            </div>
            <p>{currMaxTemp}°</p>
        </div>
    )
}

export default TemperatureBar;
