import React, { useEffect, useState } from "react";
import axios from "axios";

interface Props {
    latitude: number;
    longitude: number;
}

const WeatherCard: React.FC<Props> = ({ latitude, longitude }) => {
    const API_WEATHER = `https://api.open-meteo.com/v1/forecast?latitude=${latitude}&longitude=${longitude}&hourly=temperature_2m,uv_index&daily=weathercode,temperature_2m_max,temperature_2m_min,apparent_temperature_max,apparent_temperature_min,sunrise,sunset,uv_index_max,uv_index_clear_sky_max,precipitation_sum,rain_sum,showers_sum,snowfall_sum,windspeed_10m_max,windgusts_10m_max,winddirection_10m_dominant&current_weather=true&timezone=auto&forecast_days=16`;
    const API_GEOLOCATION = `https://geocoding-api.open-meteo.com/v1/search?name=${city}&count=10&language=en&format=json`;

    //! 'weatherData' could be refactored to support an exact type such as an interface because 'any' bypasses type-checking and is anti-typescript
    const [weatherData, setWeatherData] = useState<any | undefined>(undefined);
    const [searchQuery, setSearchQuery] = useState<string | undefined>("");
    const [searchQueryResponse, setSearchQueryResponse] = useState<string[] | undefined >([]);
    const [searchQueryInputSelected, setSearchQueryInputSelected] = useState<boolean | undefined>(false);
    var [city, setCity] = useState<string | undefined>("");

    useEffect(() => {
        const fetchWeatherData = async () => {
            try {
                const response = await axios.get(API_WEATHER);
                console.log(response.data);
                setWeatherData(response.data);
            } catch (error) {
                console.error("Error fetching weather data. " + error);
            }
        };
        fetchWeatherData();
    }, []);

    const handleSearchQueryChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setSearchQuery(event.target.value);
    };

    const handleSearchQuerySelection = () => {
        setSearchQueryInputSelected(!searchQueryInputSelected);
    };

    const handleOnSubmit = () => {
    
    };

    return (
        <React.Fragment>
            <div className="weather-card">
                <div className="weather-card-search">
                    <form
                        className="weather-card-search-form"
                        onSubmit={handleOnSubmit}
                    >
                        <label>
                            <input
                                className="weather-card-search-box"
                                type="text"
                                value={searchQuery}
                                onChange={handleSearchQueryChange}
                                onSelect={handleSearchQuerySelection}
                                placeholder={true}
                            ></input>
                        </label>
                    </form>
                </div>
                {weatherData && (
                    <React.Fragment>
                        <div className="weather-card-current-main">
                            <p>{searchQuery}</p>
                            <p>{weatherData.current_weather.temperature}â„ƒ</p>
                        </div>
                        <div className="weather-card-hourly"></div>
                        <div className="weather-card-daily"></div>
                    </React.Fragment>
                )}
            </div>
        </React.Fragment>
    );
};

export default WeatherCard;
